package Pakage;

public class Forelse {
	
	
	static int a=1;
	

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		
		
		for(a=1;a<=100;a++) {
			
			if(a % 2==0) {
				System.out.println(a+  "(even number)");
			}
			else {
				
				
				System.out.println(a+ "(odd number)");

				
			}
		}
	}

}
